
create table #Msg (Grp1ID int,Grp1Name nvarchar(300), Grp2ID int ,Grp2Name nvarchar(300),Grp3ID int,Grp3Name nvarchar(300),MsgID int,MsgName nvarchar(300));




select * from #Msg

select Grp1ID ,MsgID ,MsgName
from (
select Grp1ID ,MsgID ,MsgName , RN = row_number() over (partition by Grp1ID ORDER BY newid())
from #Msg) as T
where RN=1

select top 1 newid() as id,  dc.ConsultantKEY from dm.DimConsultant dc  order by 1  
